async function removeInput(id) {
    const index = inputs.findIndex(i => i.element.id == id);
    const input = inputs[index];
    inputs.splice(index, 1);
    await remove(input.container.id);
}

async function removeVariable(id) {
    const index = variables.findIndex(i => i.nameInput.id == id);
    const variable = variables[index];
    variables.splice(index, 1);
    await remove(variable.container.id);
}

async function addInput(role, options) {
    options ??= {};

    let element;
    if (options.url == null) {
        element = createInput(textInputType, {
            placeholder: `Enter ${role} message...`,
            defaultValue: options.text,
        });
    } else {
        element = createInput(imageInputType, {
            placeholder: `Enter url...`,
            defaultValue: options.url,
        });
    }

    const container = createGroup([
        element,
    ], {
        rightElements: [createSimpleButton(createPresetIcon("close", { title: "Delete" }), () => removeInput(element.id))]
    });

    inputs.push({ role, element, container });
    show(container, { insertAfter: inputDivider.id, noAccept: true });
}

async function _getNextValidNumber() {
    const allInputs = await readAll();
    let takenNumbers = new Set();
    for (const variable of variables) {
        const nameInput = variable.nameInput;
        const name = allInputs[nameInput.id].text;

        let parts = name.split('$var');
        if (parts.length === 2) {
            let number = parseInt(parts[1].substring(0, parts[1].length - 1));
            if (!isNaN(number)) {
                takenNumbers.add(number);
            }
        }
    }
    let nextValidNumber = 1;
    while (takenNumbers.has(nextValidNumber)) {
        nextValidNumber++;
    }

    return nextValidNumber;
}

async function addVariable(options) {
    options ??= {};

    let lastValue = options.name ?? ('$' + 'var' + await _getNextValidNumber() + '$');
    const nameInput = createInput(textInputType, {
        placeholder: "Enter variable name...",
        defaultValue: lastValue,
        onValidate: async (element) => {
            let text = element.text;
            text = text.trim();

            if (!text || lastValue == text) return;
            if (text == '$') {
                if (lastValue.length == 0) {
                    log(1);
                    text = '$' + 'var' + await _getNextValidNumber() + '$';
                } else {
                    text = '';
                }
            } else {
                if (!text.startsWith('$')) text = '$' + text;
                if (!text.endsWith('$')) text += '$';
            }

            lastValue = text;
            update(element.id, { text, retainSelection: true });
        }
    });
    const valueInput = createInput(textInputType, {
        placeholder: "Enter variable value...",
        defaultValue: options.value,
    });
    const container = createGroup([
        nameInput,
        valueInput,
    ], {
        rightElements: [createSimpleButton(createPresetIcon("close", { title: "Delete" }), () => removeVariable(nameInput.id))]
    });

    variables.push({ container, nameInput, valueInput });
    show(container, { insertAfter: variableDivider.id, noAccept: true });
}

function createMessageElement(message) {
    if (message.role == userRole) {
        if (message.url) {
            return createImage(message.url);
        } else {
            return createText(paragraphType, `User:\n${message.prompt}`, { bordered: true });
        }
    } else if (message.role == assistantRole) {
        return createMarkdown(message.prompt);
    } else if (message.role == systemRole) {
        return createText(paragraphType, `System:\n${message.prompt}`, { bordered: true });
    }
}

async function run() {
    const allInputs = await readAll();

    const variableValues = [];
    // Handle variables
    for (const variable of variables) {
        const nameInput = variable.nameInput;
        const valueInput = variable.valueInput;

        const name = allInputs[nameInput.id].text;
        const value = allInputs[valueInput.id].text;

        variableValues.push({ name, value });
    }

    // Replace variables in inputs
    for (const input of inputs) {
        let prompt = input.element.type == imageInputType ? allInputs[input.element.id].url : allInputs[input.element.id].text;
        for (const variable of variableValues) {
            prompt = prompt.replaceAll(variable.name, variable.value);
        }
        let message;
        if (input.element.type == imageInputType) {
            context.push(message = toImageMessage(prompt));
        } else {
            context.push(message = toMessage(input.role, prompt));
        }

        const messageElement = createMessageElement(message);
        contextElements.push(messageElement);
        await show(messageElement, { insertBefore: navbar.id });
    }

    // Handle the main user prompt
    let prompt = allInputs[promptInput.id].text;
    for (const variable of variableValues) {
        prompt = prompt.replaceAll(variable.name, variable.value);
    }
    const promptMessage = toUserMessage(prompt);
    context.push(promptMessage);
    const promptMessageElement = createMessageElement(promptMessage);
    contextElements.push(promptMessageElement);
    await show(promptMessageElement, { insertBefore: variableDivider.id });

    // Show assistant message stream target
    const assistantMessageElement = createMarkdown();
    contextElements.push(assistantMessageElement);
    await show(assistantMessageElement, { insertBefore: variableDivider.id });

    const result = await chat(context, { id: assistantMessageElement.id, model: allInputs[modelSelect.id].value });
    context.push(toAssistantMessage(result));
}

async function exportContext(asObject = false) {
    if (asObject) {
        return context;
    }

    const content = JSON.stringify(context, null, 2);
    if (download) await requestFileDownload("context.json", commonMimeTypes.json, content);
    return content;
}

async function importContext(newContext = null) {
    const importInput = createInput(fileInputType, {
        allowedExtensions: [".json"],
        selectDescription: "Select context JSON file",
    });

    if (newContext == null) {
        try {
            const result = await show(createGroup([
                createTitle("Import Context"),
                importInput
            ]), { location: dialogLocation });
            file = result.files[0];
        } catch (error) {
            await setStatus("Import canceled.");
            return;
        }

        const fileContent = file.text;

        try {
            newContext = JSON.parse(fileContent);
        } catch (error) {
            await setStatus("Failed to parse the JSON file. provide a valid context JSON file.");
            return;
        }
    }


    context.length = 0;
    contextElements.forEach(e => remove(e.id));
    contextElements.length = 0;
    for (const message of newContext) {
        context.push(message);
        const messageElement = createMessageElement(message);
        contextElements.push(messageElement);

        await show(messageElement, { insertBefore: navbar.id });
    }

    await setStatus("Context imported successfully.");
}

async function exportSetup(asObject = false) {
    const allInputs = await readAll();

    const variableValues = [];
    // Handle variables
    for (const variable of variables) {
        const nameInput = variable.nameInput;
        const valueInput = variable.valueInput;

        const name = allInputs[nameInput.id].text;
        const value = allInputs[valueInput.id].text;

        variableValues.push({ name, value });
    }

    // Handle inputs
    const prompts = [];
    for (const input of inputs) {
        let message = {
            role: input.role,
        }

        if (input.element.type == imageInputType) {
            message.url = allInputs[input.element.id].url;
        } else {
            message.text = allInputs[input.element.id].text;
        }

        prompts.push(message);
    }

    const prompt = allInputs[promptInput.id].text;
    const context = { variables: variableValues, prompts, prompt };
    if (asObject) {
        return context;
    }

    const content = JSON.stringify(context, null, 2);
    await requestFileDownload("setup.json", commonMimeTypes.json, content);

    return content;
}

async function importSetup(newSetup = null) {
    const importInput = createInput(fileInputType, {
        allowedExtensions: [".json"],
        selectDescription: "Select setup JSON file",
    });

    if (newSetup == null) {
        try {
            const result = await show(createGroup([
