            ]), { location: dialogLocation });
            file = result.files[0];
        } catch (error) {
            await setStatus("Import canceled.");
            return;
        }

        const fileContent = file.text;

        try {
            newSetup = JSON.parse(fileContent);
        } catch (error) {
            await setStatus("Failed to parse the JSON file. provide a valid setup JSON file.");
            return;
        }
    }


    for (const variable of variables) {
        await remove(variable.container.id);
